import * as ActionTypes from './ActionType';

export default (state = "计数： ", action) => {
    switch(action.type) {
        case ActionTypes.INCREMENT:
            return "上一步操作：增加，计数： ";
        case ActionTypes.DECREMENT:
            return "上一步操作：减少，计数： ";
        default:
            return state;
    }
};
